sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	var defaultMethod = g_form.getValue('default_method');
	if(newValue !== defaultMethod && newValue === 'ire') {
		var className = g_form.getValue('class_name');
		g_form.addInfoMessage('Please verify that the IRE Identification Rule for ' + className + ' has been configured per the LSP2P documentation'); 
	} else {
		g_form.clearMessages();
	}
}
